package com.megha.godsendapp

import android.content.Intent
import android.net.Uri
import android.support.v7.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import kotlinx.android.synthetic.main.activity_detail.*
import kotlinx.android.synthetic.main.activity_menu.*

class MenuActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_menu)
        menutoolbar.setTitle("MENU")
        menutoolbar.setNavigationIcon(R.drawable.ic_action_back)
        menutoolbar.setNavigationOnClickListener {

            onBackPressed();
        }

        objectivecard.setOnClickListener {
            var intent = Intent(this, ObjectiveActivity::class.java)
            startActivity(intent)
        }

        eventcard.setOnClickListener {
            var intent = Intent(this, EventsActivity::class.java)
            startActivity(intent)
        }

        contactcard.setOnClickListener {
            var intent = Intent(this, ContactActivity::class.java)
            startActivity(intent)
        }

        membercard.setOnClickListener {
            var intent = Intent(this, MembersActivity::class.java)
            startActivity(intent)
        }

        upcomingcard.setOnClickListener {
            var intent = Intent(this, UpcomingEvent::class.java)
            startActivity(intent)
        }

        videocard.setOnClickListener {
            var intent = Intent(this, VideoActivity::class.java)
            startActivity(intent)
        }

        Glide.with(this).load("https://www.buses.org/assets/images/uploads/general/belong-icon.png")
                .into(memberimage);
        Glide.with(this).load("data:image/jpeg;base64,")
                .into(objectiveimage);
        Glide.with(this).load("https://cdn2.iconfinder.com/data/icons/seo-smart-pack/128/grey_new_seo2-41-512.png")
                .into(contactimg);
        Glide.with(this).load("https://www.manisteelibrary.org/images/events-calendar.png/@@images/image.png")
                .into(eventimg);
        Glide.with(this).load("https://www.questar.org/wp-content/uploads/2019/01/upcoming-events.png")
                .into(upcomingimg);
        Glide.with(this).load("http://icons.iconarchive.com/icons/jommans/emluator/256/Video-icon.png")
                .into(videoimg);


        tv_develop.setOnClickListener {
            val intent = Intent()
            intent.action = Intent.ACTION_VIEW
            intent.addCategory(Intent.CATEGORY_BROWSABLE)
            intent.data = Uri.parse("https://github.com/Megha307")
            startActivity(intent)
        }
    }
}
